import React, { Component } from 'react'
import './App.css'
import Navbar from './Navbar/Navbar.js'
import Presentation from './component/Main/Presentation.js'
import Block from './component/Main/Block.js'
import SimulPhone from './component/Main/SimulPhone.js'
import Sponsor from './component/Main/Sponsor.js'
// import Formation from './component/Main/Formation.js'
import Formations from './component/Main/Formations.js'
import Formateurs from './component/Main/Formateurs.js'
import Contact from './component/Contact/Contact.js'
import Footer from './component/Footer/Footer.js'
import Loading from './component/Loading/Loading.'
import '@fortawesome/fontawesome-free/css/all.min.css'
import 'bootstrap-css-only/css/bootstrap.min.css'
import 'mdbreact/dist/css/mdb.css'

// Firebase
import * as firebase from 'firebase'
import config from './firebase'

class App extends Component {
  constructor () {
    super()
    // initialiser component en lui passant la config firebase.js
    firebase.initializeApp(config)
    this.state = {
      loading: true
    }
  }

  // recupere les infos de la bdd quand le component se monte
  componentWillMount () {
    const ref = firebase.database().ref('laurent')

    ref.on('value', snapshot => {
      // MAJ du state
      this.setState({
        data: snapshot.val(),
        loading: false
      })
    })
  }

  render () {
    if (this.state.loading) {
      return <h1>Chargement</h1>
    }

    return (
      <div className='App'>
        <header className='App-header'>
          <Navbar />
          <Presentation />
          <Block />
          <SimulPhone />
          <Sponsor />
          {/* <Formation /> */}
          <Formations />
          <Formateurs />
          <Contact infos={this.state.data.contact} />
          <Footer />
        </header>
      </div>
    )
  }
}

export default App
