import React, { Component } from 'react'

import * as firebase from 'firebase'
class AdminForm extends Component {
  constructor (props) {
    super(props)
    const data = this.props.data
    this.state = { data }
  }

    //fonction pour recuperer les infos textarea
    handleChange = event => {
        //Copie de data
        const data = {...this.state.data}
        //Modifier le content
        data.content = event.target.value
        // Maj data
        this.setState({ data })
      }

  render () {
    return (
      <section>
        <textarea
          rows='10'
          value={this.state.data.content}
           // Event pour lancer fonction lors d'un changement du textarea
           onChange={this.handleChange}
        />
      </section>
    )
  }
}
export default AdminForm
