import React from 'react'
import './BlockFormations.css'
import { Container, Row, Col } from 'react-bootstrap'
import { MDBBtn } from 'mdbreact'
import Flip from 'react-reveal/Flip'
// import emailjs from 'emailjs-com'

export default function BlockFormations () {
  // EmailJs
//   function sendEmail (e) {
//     e.preventDefault()
//     alert('Votre demande à bien étais envoyé !')

//     emailjs.sendForm('service_contact', 'template_5I0uoTLK', e.target, 'user_IcSWi6VT9B6GUf6FsNO6B')
//       .then((result) => {
//         console.log(result.text)
//       }, (error) => {
//         console.log(error.text)
//       })
//   }

  return (
    <Container className='mb-5 mt-5' id='block'>
      <Flip top duration={1500} delay={1500}>
        <h3>Pour plus d'information sur les périodes de formations, entrez votre mail dans le formulaire ci-dessous. Et vous recevrez la plaquette des formations en pièce jointe.</h3>
        <MDBBtn href='#contact' color='white'>Contact</MDBBtn>
        {/* <Fade duration={500} delay={500}> */}
        <section className='fondimage my-md-5'>
          <div className='rgba-black-strong rounded p-5'>
            <h2 className='text-center font-weight-bold mt-3 mb-5'>Contactez moi</h2>
            <form className='contact-form mx-md-5' method='POST' data-netlify='true' onSubmit={sendEmail} novalidate>
              <Row>
                <Col md={6} className=' mb-4'>
                  <div className='card'>
                    <div className='card-body px-4'>
                      <div className='md-form md-outline mt-0'>
                        <input type='text' name='from_name' className='form-control' placeholder='Nom' required />
                        <label htmlFor='name' />
                      </div>
                      <div className='md-form md-outline'>
                        <input type='email' name='user_email' className='form-control' placeholder='Email' required />
                        <label htmlFor='email' />
                      </div>
                      <button onSubmit={sendEmail} type='submit' value='Send' className='btn blue-rgba btn-md btn-block ml-0 mb-0 text-white'>Envoyez</button>
                    </div>
                  </div>
                </Col>
              </Row>
            </form>
          </div>
        </section>
        {/* </Fade> */}
      </Flip>
    </Container>

  )
}
