import React from 'react'
import './Presentation.css'
import Card from 'react-bootstrap/Card'
import { Spring } from 'react-spring'

class Presentation extends React.Component {
  render () {
    return (
      <Spring
        from={{ opacity: 0.6, marginTop: -50 }}
        to={{ opacity: 1, marginTop: 50 }}>

        {props => (
          <Card bg='primary' text='white' className='container col-lg-9 col-md-12 mt-5'>
            <div className=''>
              <Card.Title className='text-center'>Qui sommes-nous ?</Card.Title>
              <blockquote className='blockquote mb-0 card-body'>
                <p>AnthroPi est un organisme de formation à la communication et à l’appel téléphonique utilisant un simulateur de conversation .</p>
                <p>Notre simulateur de conversation (SimulPhone ®) est un simulateur unique. Il place les apprenants dans un environnement réaliste et contrôlé. Encadrées de briefings et debriefings, nos sessions sont animées par des formateurs expérimentés exerçant dans les métiers de la communication.</p>
                <p>Nos liens avec les laboratoires de recherche nous permettent de proposer des formations sur des bases scientifiques.</p>
                <p>N’hésitez pas à nous contacter pour une démonstration sur site !</p>
                <p className='text-right'><strong>La Team AnthroPi !</strong></p>
              </blockquote>
            </div>
          </Card>
        )}
      </Spring>

    )
  }
}

export default Presentation
