import React from 'react'
import './BlockFormations.css'
import { Container, Row, Col } from 'react-bootstrap'
import Flip from 'react-reveal/Flip'
import emailjs from 'emailjs-com'

export default function BlockFormations () {
  // EmailJs
  function sendEmail (e) {
    e.preventDefault()
    alert('Votre demande à bien étais envoyé !')

    emailjs.sendForm('service_contact', 'email_anthropi', e.target, 'user_IcSWi6VT9B6GUf6FsNO6B')
      .then((result) => {
        console.log(result.text)
      }, (error) => {
        console.log(error.text)
      })
  }

  return (
    <Container className='mb-5 mt-5' id='blockformation'>
      <Flip top duration={1500} delay={1500}>
        <h4>Entrez votre mail dans le formulaire ci-dessous, et vous recevrez la plaquette des périodes de formations en pièce jointe.</h4>
        {/* <Fade duration={500} delay={500}> */}

        <form className='contact-form mt-5' method='POST' data-netlify='true' onSubmit={sendEmail} novalidate>
          <Row>
            <Col className='flex-center mb-4'>
              <div className='card'>
                <div className='card-body'>
                  <div className='md-form  mt-0'>
                    <input type='text' name='from_name' className='form-control' placeholder='Nom' required />
                    <label htmlFor='name' />
                  </div>
                  <div className='md-form'>
                    <input type='email' name='user_email' className='form-control' placeholder='Email' required />
                    <label htmlFor='email' />
                  </div>
                  <button onSubmit={sendEmail} type='submit' value='Send' id='btncolor' className='btn btn-md btn-block ml-0 mb-0 text-white'>Envoyez</button>
                </div>
              </div>
            </Col>
          </Row>
        </form>
        {/* </Fade> */}
      </Flip>
    </Container>

  )
}
